import React, { useEffect } from "react";
import styled from "styled-components";

function Followers(props) {
  const { follower } = props;
  useEffect(() => {
    console.log(follower);
  }, [follower]);

  const CardContainer = styled.div`
    display: flex;
    margin: 40px;
    flex-wrap: wrap;
  `;

  const Image = styled.div`
    text-align: center;
    border-radius: 10px;
  `;

  const Card = styled.div`
    background-color: rgba(247, 247, 247, 0.8);
    border: 1px solid #443e3e;
    box-shadow: 0px 5px 20px rgb(71, 71, 71);
    border-radius: 15px;
    width: 500px;
    margin: 0 auto;
  `;

  const Username = styled.h2`
    color: #443e3e;
    font-weight: bold;
  `;

  const UserInfo = styled.p`
    color: #443e3e;
  `;

  return (
    <CardContainer>
      {props.follower.map(follower => (
        <Card>
          <Image
            className="avatar"
            src={follower.avatar_url}
            alt="follower avatar"
          ></Image>
          <Username>Username: {follower.login}</Username>
          <UserInfo>
            Profile: <a href={follower.html_url}>{follower.html_url}</a>
          </UserInfo>
          <UserInfo>Followers: {follower.followers_url.length}</UserInfo>
          <UserInfo>Following: {follower.following_url.length}</UserInfo>
        </Card>
      ))}
    </CardContainer>
  );
}

export default Followers;
