import React from "react";
import axios from "axios";

class Follower extends React.Component {
  state = {
    followers: []
  };

  componentDidMount() {
    axios
      .get("https://api.github.com/users/briworkman/followers")
      .then(response => {
        response.data.foreach(follower =>
          axios
            .get(`https://api.github.com/users/${follower.login}`)
            .then(response => {
              console.log(response.data);
              this.setState({
                followers: response.data
              });
            })
            .catch(err => {
              console.log(err);
            })
        );
      });
  }

  render() {
    return (
      <div className="App">
        <h1>{this.state.followers.name}</h1>
        <h3>{this.state.followers.login}</h3>
        <p>Location: {this.state.followers.location}</p>
        <p>
          Profile:{" "}
          <a href={this.state.followers.html_url}>
            {this.state.followers.html_url}
          </a>
        </p>
        <p>Followers: {this.state.followers.followers}</p>
        <p> Following: {this.state.followers.following}</p>
        <p>Bio: {this.state.followers.bio}</p>
      </div>
    );
  }
}

export default Follower;
