import React from "react";
import ReactDOM from "react-dom";
import axios from "axios";
import Followers from "./Followers";
import styled from "styled-components";

class App extends React.Component {
  state = {
    user: [],
    followers: []
  };

  componentDidMount() {
    axios
      .get("https://api.github.com/users/briworkman")
      .then(response => {
        console.log(response);
        this.setState({
          user: response.data
        });
      })
      .catch(err => {
        console.log(err);
      });

    axios
      .get("https://api.github.com/users/briworkman/followers")
      .then(res => {
        this.setState({
          followers: res.data
        });
        console.log(this.state.followers);
      })
      .catch(err => console.log(err));
  }

  render() {
    const CardContainer = styled.div`
      display: flex;
      margin: 40px;
      flex-wrap: wrap;
    `;

    const Card = styled.div`
      background-color: rgba(247, 247, 247, 0.8);
      border: 1px solid #443e3e;
      box-shadow: 0px 5px 20px rgb(71, 71, 71);
      border-radius: 15px;
      width: 500px;
      margin: 0 auto;
    `;

    const Image = styled.img`
      text-align: center;
      border-radius: 10px;
    `;

    const Username = styled.h2`
      color: #443e3e;
      font-weight: bold;
    `;

    const UserInfo = styled.p`
      color: #443e3e;
    `;
    return (
      <CardContainer>
        <Card>
          <Image
            src={this.state.user.avatar_url}
            alt="Follower Profile Picture"
          ></Image>
          <Username>Username: {this.state.user.login}</Username>
          <UserInfo>
            Profile:{" "}
            <a href={this.state.user.html_url}>{this.state.user.html_url}</a>
          </UserInfo>
          <UserInfo>Followers: {this.state.user.followers}</UserInfo>
          <UserInfo> Following: {this.state.user.following}</UserInfo>
        </Card>
        <Followers follower={this.state.followers} />
      </CardContainer>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
